            await app.fetch.post(basic_url + hobbyUserUrl + `?put=true&id=` + app.dataUser.userID,hobbysArray, false,(err,data) => {
                if(err){
                    console.log(JSON.stringify(err));
                    return alert("Vui lòng thửu lại sau")
                }
            })
            return app.start();
        }
    } 
    
}
