var nav_clicks_data = {
    "ket_noi" : (()=>{
        var body = "";
        ket_noi.forEach( a => {
            body += `
            <div class = "connection">
                <img src ="${a.img}"/>
                <span class = "connection_span"><h3>${a.name}</h3></span>
            </div>
            `
        })
        return body;
    })(),
    "tin_nhan": (()=>{
        var bodymess = "";
        tin_nhan.forEach( a => {
            bodymess += `
            <div class = "messger active-message">
                <img src ="${a.img}"/>
                <div class = "messger-content">
                    <span class = "connection_span"><h3>${a.name}</h3></span>
                    <p>${a.you ? "<i class='bx bx-arrow-back'></i>" : ""}${a.body}</p>
                </div>
            </div>
            `
        })
        return bodymess;
    })()
};
