        app.showDivs(app.slideIndex += n);
    },
    showDivs: (n)=>{
        var i;
        var x = qSA('.slide');
        var x_dot = qSA(".slide-dot");
        if (n > x.length) {app.slideIndex = 1}
        if (n < 1) {app.slideIndex = x.length}
        for (i = 0; i < x.length; i++) {
            x[i].style.display = "none";  
            x_dot[i].style.background = "rgb(112 112 112)";
        }
        x[app.slideIndex-1].style.display = "block"; 
        x_dot[app.slideIndex-1].style.background = "#fff";  
    },
    nextprofile: ()=>{ 
        app.profile_account.splice(0,1);
        localStorage.setItem("profile", JSON.stringify(app.profile_account));
        app.render()
    },
    checklogin: function (){
        if(!app.login){
            var interestHTML = "";
            qS(".nav_content").innerHTML = `
            <div class="login-register">
                    <div id = "form-login" class = "show">
                        <h2>Đăng nhập</h2>
                        <form class="form" id = "login">
                            <input type="email" placeholder="Email"  />
                            <input type="password" placeholder="Mật khẩu"  />
                            <button> Đăng nhập </button>
                            <div class = "form_login-button" id = "covent_dk"> <p> Đăng ký </p></div>
                        </form>
                    </div>
                    <div id = "form-register">
                        <h2>Đăng Ký</h2>
                        <form class="form" id = "register">
                            <input type="email" placeholder="Email" />
                            <input type="password" placeholder="Mật khẩu" />
                            <input type="password" placeholder="Nhập lại mật khẩu"  />
                            <input type="text" placeholder="Địa Chỉ"  />
                            <input type="number" placeholder="Tuổi" onkeypress="return isNumberKey(event)" 
                            maxlength="5" />
                            <h5 style = "margin-left: 10%; ">Sở thích</h5>
                            <div id = "js_interest" class = "form_register-interest"></div>
                            <button> Đăng ký </button>
                            <div class = "form_login-button" id = "covent_dn"> <p> Đăng nhập </p></div>
                        </form>
                    </div>
                </div>`;
            app.get(basic_url + Interests,(err,data) => {
                if(err) return console.log(err);
                for(var i=0; i<data.length; i++){
                    interestHTML += `<div><input type="checkbox" id="hobby_${i}" name="vehicle1" value="${data[i].id}">
                    <label for="hobby_${i}"> ${data[i].hobby} </label></div> `
                }
                qS("#js_interest").innerHTML = interestHTML;
                return qS("#form-login").style.opacity = "1";
            })
            
            qS(".nav__hearder--profile").innerHTML = " ";
        }else{
            qS(".nav_content").innerHTML = `
            <div class="nav_content-button">
                <button type="button" class="nav-button nav-active" data-value = "ket_noi"><spam>Kết Nối</spam></button>
                <button type="button" class="nav-button" data-value = "tin_nhan"><spam>Tin Nhắn</spam></button>
                </div>
            <div class="nav__content-main" id = "js_nav_content_main"></div>
            `
            qS("#js_nav_content_main").innerHTML = nav_clicks_data["ket_noi"];

            qS(".nav__hearder--profile").innerHTML = `<a href="#">
            <img src = "https://scontent.fhph1-3.fna.fbcdn.net/v/t1.6435-1/c1.64.198.199a/p200x200/141433767_1108249559690088_990288042436184426_n.jpg?_nc_cat=107&ccb=1-5&_nc_sid=7206a8&_nc_ohc=5yxEM9Iimv0AX_4k7Ox&_nc_ht=scontent.fhph1-3.fna&oh=00_AT-vqYdzU7_RG4YwBws_REoHUQ_G1kzl29AkfIijrbwnXg&oe=61FCEA38">
            <h3>Đông</h3>
        </a>`
        }
    },
    render: ()=>{
        if(app.profile_account == null){
            localStorage.setItem("profile", JSON.stringify(this.profile));
            app.profile_account = profile;
        }
        if(app.profile_account.length != 0){
            qS("#js_imgs_profile").innerHTML =   qS('#js_menu_imgs').innerHTML =  qS('#js_main_profile').innerHTML =" ";
            var data = app.profile_account[0];
            for(var index = 0; index < data.img.length; index++){
                qS("#js_imgs_profile").innerHTML +=`<img class="slide slide-${index}" src="${data.img[index]}"></img>`;
                qS('#js_menu_imgs').innerHTML += `<label class="slide-dot"></label>`
            }
            qS('#js_main_profile').innerHTML = ` <h1>${data.name} ${data.age ? "<span>" + data.age +"</span>" : ""}</h1> <div class = "content__profile-main-hobbys"></div>`;
            if(data.sothich){
                data.sothich.forEach(a => {
                    qS('.content__profile-main-hobbys').innerHTML += `<div>${a}</div>`
                }) 
            }
            app.showDivs(app.slideIndex);
        }else{
            qS("#js_imgs_profile").innerHTML =   qS('#js_menu_imgs').innerHTML =  qS('#js_main_profile').innerHTML =" ";
            qS("#js_imgs_profile").innerHTML = `<h1>Bạn đã hết người tương hợp</h1>`
        }
    },
    handEvent: ()=>{
        //chọn tin nhắn hay kết nối
        var nav_clicks = qSA(".nav-button");
        for (const item of nav_clicks) {
            item.onclick = (e) => {
                nav_clicks.forEach(a => a.classList.remove("nav-active"))
                item.classList.add("nav-active");
                qS("#js_nav_content_main").innerHTML = nav_clicks_data[item.dataset.value]; 
            }
        }
        //đăng ký đăng nhập
        if(!app.login){
            qS("#covent_dk").onclick = ()=> {
                qS("#form-login").style.opacity = "0";
                qS("#form-login").style.left = "100%";
                qS("#form-register").style.opacity = "1";
                qS("#form-register").style.left = "0";
            }
            qS("#covent_dn").onclick = ()=> {
                qS("#form-login").style.opacity = "1";
                qS("#form-login").style.left = "0";
                qS("#form-register").style.opacity = "0";
                qS("#form-register").style.left = "100%";
            }
        }
        //Khi nhấn keybroad
        qS("body").addEventListener('keyup', (e)=>{
            switch(e.code){
                case "ArrowUp":
                    break;
                case "ArrowDown":
                    renderUser()
                break;
                case "ArrowLeft":
                    app.nextprofile()
                 break;
                case "ArrowRight":
                    app.nextprofile()
                    break;
                case "Space":
                    app.plusDivs(1);
                    break;
            }
        });
    },
    get: function (url, callback) {
        return fetch(url,{
            method : "GET",
            mode: 'cors'
        })
        .then(response => response.json())
            .then(data => callback(null,data))
            .catch(function(error) {
                callback(error)
            });
    },
    api: function () {
        this.get(basic_url + Interests,(err,data) => {
            if(err) return console.log(err);
            return console.log(data)
        })
    },
    start: function(){
        //lấy api
        this.api();
        //check login
        this.checklogin();

        this.render();
        //nghe các sự kiện click
        this.handEvent();
    }
}
app.start();


function noneitems(){
    qSA(".content_hd-item").forEach(a => a.classList.toggle("show"));
}
